def run_part_1(path):
  floor = 0
  for step in path:
    floor += -1 if step == ')' else 1
  return floor

def run_part_2(path):
  floor = 0
  index = 0
  for step in path:
    index += 1
    floor += -1 if step == ')' else 1
    if floor < 0:
      return index
  return index

def tests_part_1():
  assert run_part_1('(())') == 0
  assert run_part_1('()()') == 0
  assert run_part_1('(((') == 3
  assert run_part_1('(()(()(') == 3
  assert run_part_1('))(((((') == 3
  assert run_part_1('())') == -1
  assert run_part_1('))(') == -1
  assert run_part_1(')))') == -3
  assert run_part_1(')())())') == -3

def tests_part_2():
  assert run_part_2(')') == 1
  assert run_part_2('()())') == 5

if __name__ == '__main__':
  tests_part_1()
  print(f'Part 1: {run_part_1(input)}')
  tests_part_2()
  print(f'Part 2: {run_part_2(input)}')